import './TelaBusca.css'
import BarraPesquisa from "../../compontente/BarraPesquisa/BarraPesquisa";
import Categoria from "../../compontente/Categoria/Categoria";
import axios from "axios";
import { useEffect, useState } from "react";
function TelaBusca() {
  const [Livros, setLivros] = useState([{}]);
  const [Pesquisa, setPesquisa] = useState();
  const [Filtro, setFiltro] = useState();
  
  
  useEffect(() => {
    if(Pesquisa === ""){

    axios
      .get("http://localhost:8080/Book")
      .then((resposta) => {
        setLivros(resposta.data);
      })
      .catch((error) => {
        console.log(error);
      });

    }else{
      if(Filtro === "" || Filtro === null || Filtro === undefined || Filtro === "Livro"){

      axios
      .get("http://localhost:8080/Name/"+Pesquisa)
      .then((resposta) => {
        setLivros(resposta.data);
      })
      .catch((error) => {
        console.log(error);
      });
      
    }else{
      if(Filtro === "Genero"){

        axios
        .get("http://localhost:8080/Topic/"+Pesquisa)
        .then((resposta) => {
          setLivros(resposta.data);
        })
        .catch((error) => {
          console.log(error);
        });

      }else{
        if(Filtro === "Autor"){

          axios
          .get("http://localhost:8080/Author/"+Pesquisa)
          .then((resposta) => {
            setLivros(resposta.data);
          })
          .catch((error) => {
            console.log(error);
          });

        }
      }
    }
    }
  }, [Pesquisa]);
  var listaTopicos = [];

  Livros.forEach((livro) => {
    if (
        !listaTopicos.includes(livro.topic)) {
      listaTopicos.push(livro.topic);
    }
  });
  return (
    <div className="TelaBusca">
      <BarraPesquisa aoPesquisar={(pesquisa) => setPesquisa(pesquisa)} alteraFiltro={(filtro) => setFiltro(filtro)}/>
      {listaTopicos.map((topico) => {
        return (
          <Categoria
            key={topico}
            categoria={topico}
            livro={Livros.filter((conteudo) => {
              return conteudo.topic === topico;
            })}
          />
        );
      })}
    </div>
  );
}

export default TelaBusca;
