import "./TelaBusca.css";
import BarraPesquisa from "../../compontente/BarraPesquisa/BarraPesquisa";
import Categoria from "../../compontente/Categoria/Categoria";
import axios from "axios";
import { useEffect, useState } from "react";
function TelaBusca() {
  const [Livros, setLivros] = useState([{}]);
  const [Pesquisa, setPesquisa] = useState("");
  const [EstanteEvento, setEstanteEvento] = useState([{}]);
  const [Filtro, setFiltro] = useState();
  var listaTopicos = [];
  console.log("Height;" + window.innerHeight + "/ Width: " + window.innerWidth);
  useEffect(() => {
    if (Pesquisa === "") {
      axios
        .get("http://localhost:8080/Book")
        .then((resposta) => {
          setLivros(resposta.data);
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      if (
        Filtro === "" ||
        Filtro === null ||
        Filtro === undefined ||
        Filtro === "Livro"
      ) {
        axios
          .get("http://localhost:8080/Name/" + Pesquisa)
          .then((resposta) => {
            setLivros(resposta.data);
          })
          .catch((error) => {
            console.log(error);
          });
      } else {
        if (Filtro === "Assunto") {
          axios
            .get("http://localhost:8080/Topic/" + Pesquisa)
            .then((resposta) => {
              setLivros(resposta.data);
            })
            .catch((error) => {
              console.log(error);
            });
        } else {
          if (Filtro === "Autor") {
            axios
              .get("http://localhost:8080/Author/" + Pesquisa)
              .then((resposta) => {
                setLivros(resposta.data);
              })
              .catch((error) => {
                console.log(error);
              });
          } else {
            if (Filtro === "Biblioteca") {
              axios
                .get("http://localhost:8080/Library/" + Pesquisa)
                .then((resposta) => {
                  setLivros(resposta.data.books);
                  if (resposta.data.bookshelfs != null) {
                    setEstanteEvento(resposta.data.bookshelfs);
                  }
                })
                .catch((error) => {
                  console.log(error);
                });
            }
          }
        }
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [Pesquisa]);

function criarEstanteEvento() {
if(EstanteEvento != null){
  EstanteEvento.map((estante, i) => {
    if (estante != null) {
      return (
        <Categoria
          event = {true}
          key={estante.identifier}
          categoria={estante.event}
          livro={estante.books}
        />
      );
    }else{return <></>}
  })
}
}

  Livros.forEach((livro) => {
    if (!listaTopicos.includes(livro.topic)) {
      listaTopicos.push(livro.topic);
    }
  });
  return (
    <div className="TelaBusca">
      <BarraPesquisa
        aoPesquisar={(pesquisa) => setPesquisa(pesquisa)}
        alteraFiltro={(filtro) => setFiltro(filtro)}
      />
      {criarEstanteEvento}

      {listaTopicos.map((topico, i) => {
        return (
          <Categoria
            event = {false}
            key={i}
            categoria={topico}
            livro={Livros.filter((conteudo) => {
              return conteudo.topic === topico;
            })}
          />
        );
      })}
    </div>
  );
}

export default TelaBusca;
