import './App.css';
import BarraPesquisa from './compontente/BarraPesquisa/BarraPesquisa';
import Cabecalho from './compontente/Cabecalho/Cabecalho';
import Cateoria from './compontente/Categoria/Categoria';
import axios from "axios";
import { useEffect, useState } from "react";
function App() {
  const [Livros, setLivros] = useState([{}]);
  var Topicos = Livros.filter((livro ,i , s) => {
    console.log(livro, " eae " ,i, " eae "  , s)
    console.log("aaaaaa")

    return s.indexOf(livro.topic) === i

  })
  const arr = [1, 1, 2, 2, 3, 3, "teste", "teste"];
  const arrUnique = [...new Set(arr)];
  console.log(arrUnique)

  useEffect(() => {
    axios.get("http://localhost:8080/teste").then((resposta) => {
        console.log(resposta);
        setLivros(resposta.data);
      }).catch((error) => {
        console.log(error);
      }); }, []);

  return (

    <>
    <Cabecalho />
    <BarraPesquisa />
    {
    Topicos.map((topico) =>{
      return <Cateoria cateoria={topico} />
  })
    }
     </>


  );
}

export default App;
