import { useNavigate } from "react-router-dom";
import "./Livro.css";

function Textodestaque(texto){
  return(<h1 className="caracteristica">texto</h1>)
}
function Livro(props) {
  const navigate = useNavigate();
  function Textodestaque(texto){
    if(texto !== null && texto !== undefined){
    return(<h1 className="caracteristica">texto</h1>)
  }
  }
  function nome(nome) {
    nome = nome + "";
    if (nome.slice(0, nome.indexOf("/")).length < 30) {
      return nome.slice(0, nome.indexOf("/"));
    } else {
      return nome.slice(0, nome.indexOf("/")).substring(0, 30) + " ...";
    }
  }
  return (
    <div
      className="Livro"
      onClick={() => {
        navigate("book", { state: { data: props.livro } });
      }}
    >
      <img src={props.livro.image} alt="eae" />
      <h1>{nome(props.livro.name)}</h1>
      {Textodestaque(props.livro.attribute)}
    </div>
  );
}

export default Livro;
