import BarraPesquisa from "../compontente/BarraPesquisa/BarraPesquisa";
import Cabecalho from "../compontente/Cabecalho/Cabecalho";
import Categoria from "../compontente/Categoria/Categoria";
import axios from "axios";
import { useEffect, useState } from "react";
function TelaBusca() {
  const [Livros, setLivros] = useState([{}]);
  var [Topicos, setTopicos] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/teste")
      .then((resposta) => {
        setLivros(resposta.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  console.log(Livros)

  var listaTopicos = []
Livros.map((livro)=>{
  if(listaTopicos.find((topico) =>{
    topico = livro.topic
  })){
listaTopicos.push(livro.topico)
  }
}
)
  setTopicos(listaTopicos);
  return (
    <>
      <Cabecalho />
      <BarraPesquisa />
      {Topicos.map((topico) => {
        return (
          <Categoria key={topico}
            categoria = {topico}
            livro = {Livros.filter((conteudo) => {
                return conteudo.topic == topico;
            })}
          />
        );
      })}
    </>
  );
}

export default TelaBusca;
