import { useNavigate } from "react-router-dom";
import "./Livro.css";
import { type } from "@testing-library/user-event/dist/type";

function Livro(props) {
  const navigate = useNavigate();

  function Textodestaque(texto) {
    if (texto !== null && texto !== undefined && texto !== "") {
      return <h1 className="Caracteristica">{texto}</h1>;
    }
  }

  function nome(nome) {
    nome = nome + "";
  var book = "oi"
    if ( typeof nome == "string" && nome.slice(0, nome.indexOf("/")).length < 35 && nome.includes("/")) {
      return nome.slice(0, nome.slice(0, nome.indexOf("/")).length);
    } else {
      if(nome.includes("/") || nome.length < 35){
      return nome.substring(0, 35) + " ...";
      }else{
        return nome;
      }
    }
  }
  return (
    <div
      className="Livro"
      onClick={() => {
        navigate("book", { state: { data: props.livro } });
      }}
    >
      <img src={props.livro.image} alt="eae" />

      <h1>{nome(props.livro.name)}</h1>

      {Textodestaque(props.livro.attribute)}
    </div>
  );
}

export default Livro;
