import { useNavigate } from "react-router-dom";
import "./Livro.css";

function Livro(props) {
  const navigate = useNavigate();
  function nome(nome) {
    nome = nome + "";
    if (nome.slice(0, nome.indexOf("/")).length < 30) {
      return nome.slice(0, nome.indexOf("/"));
    } else {
      return nome.slice(0, nome.indexOf("/")).substring(0, 30) + " ...";
    }
  }
  return (
    <div
      className="Livro"
      onClick={() => {
        navigate("book", { state: { data: props.livro.id } });
      }}
    >
      <img src={props.livro.image} alt="eae" />
      <h1>{nome(props.livro.name)}</h1>
    </div>
  );
}

export default Livro;
