import "./TelaBusca.css";
import BarraPesquisa from "../../compontente/BarraPesquisa/BarraPesquisa";
import Categoria from "../../compontente/Categoria/Categoria";
import axios from "axios";
import { useEffect, useState } from "react";
function TelaBusca() {
  const [Livros, setLivros] = useState([{}]);
  const [Pesquisa, setPesquisa] = useState("");
  const [Estantes, SetEstantes] = useState([{}]);
  const [Filtro, setFiltro] = useState();
  useEffect(() => {
    if (Pesquisa === "") {
      axios
        .get("http://localhost:8080/Book")
        .then((resposta) => {
          setLivros(resposta.data);
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      if (
        Filtro === "" ||
        Filtro === null ||
        Filtro === undefined ||
        Filtro === "Livro"
      ) {
        axios
          .get("http://localhost:8080/Name/" + Pesquisa)
          .then((resposta) => {
            setLivros(resposta.data);
          })
          .catch((error) => {
            console.log(error);
          });
      } else {
        if (Filtro === "Assunto") {
          axios
            .get("http://localhost:8080/Topic/" + Pesquisa)
            .then((resposta) => {
              setLivros(resposta.data);
            })
            .catch((error) => {
              console.log(error);
            });
        } else {
          if (Filtro === "Autor") {
            axios
              .get("http://localhost:8080/Author/" + Pesquisa)
              .then((resposta) => {
                setLivros(resposta.data);
              })
              .catch((error) => {
                console.log(error);
              });
          } else {
            if (Filtro === "Biblioteca") {
              axios
                .get("http://localhost:8080/Library/" + Pesquisa)
                .then((resposta) => {
                  setLivros(resposta.data.books);
                  SetEstantes(resposta.data.bookshelfs);
                })
                .catch((error) => {
                  console.log(error);
                });
            } else {
              if (Filtro === "Classificação") {
                axios
                  .get("http://localhost:8080/Classification/" + Pesquisa)
                  .then((resposta) => {
                    setLivros(resposta.data);
                  })
                  .catch((error) => {
                    console.log(error);
                  });
              }
            }
          }
        }
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [Pesquisa]);

  var listaTopicos = [];
  Livros.forEach((livro) => {
    if (!listaTopicos.includes(livro.topic)) {
      listaTopicos.push(livro.topic);
    }
  });


  function CriaCapeta(ListaEstante) {

    if (ListaEstante != null && ListaEstante !== undefined) {
      ListaEstante.map((estante, i) => {
       
        return (
          <Categoria
            event={false}
            key={i+500}
            categoria={estante.event}
            livro={estante.books}
          />
        );
      });
    }
  }

  
  return (
    <div className="TelaBusca">
      <BarraPesquisa
        aoPesquisar={(pesquisa) => setPesquisa(pesquisa)}
        alteraFiltro={(filtro) => setFiltro(filtro)}
      />
      
   {CriaCapeta(Estantes)}
      {listaTopicos.map((topico, i) => {
        return (
          <Categoria
            event={false}
            key={i}
            categoria={topico}
            livro={Livros.filter((conteudo) => {
              return conteudo.topic === topico;
            })}
          />
        );
      })}
    </div>
  );
}


export default TelaBusca;
