import {Navigate, useNavigate} from "react-router-dom";
import "./Livro.css";


function Livro(props) {
    const navigate = useNavigate();
    
    const vai = ()=> {
        navigate('/book', { id: props.livro.id} )
    }
    function nome(nome){
        if((nome.slice(0,nome.indexOf("/"))).length<25){
            return nome.slice(0,nome.indexOf("/"))
        }else{
            return (nome.slice(0,nome.indexOf("/"))).substring(0,25) + " ..."
        }
        
    }
    return(
            <div className="Livro" onClick={vai()}>
                <img src={props.livro.image}/>
                <h1>
                    {
                        nome(props.livro.name)
                    }
                    </h1>
            </div>

     );
}

export default Livro;
