import './App.css';
import BarraPesquisa from './compontente/BarraPesquisa/BarraPesquisa';
import Cabecalho from './compontente/Cabecalho/Cabecalho';
import Cateoria from './compontente/Categoria/Categoria';
import axios from "axios";
import { useEffect, useState } from "react";
function App() {
  const [Livros, setLivros] = useState([{}]);
  const [Topicos, setTopicos] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/teste").then((resposta) => {
        console.log(resposta);
        setLivros(resposta.data);
      }).catch((error) => {
        console.log(error);
      }); }, []);

  return (

    <>
     { Livros.map(livro => {
      setTopicos(Topicos, livro.topic);
    })}
    <Cabecalho />
    <BarraPesquisa />
    {Topicos.filter( (topico, i) => {
        if(Topicos.indexOf(topico) === i){
          <Cateoria Topico={topico} />
        }
        return false;
    })} </>


  );
}

export default App;
