import "./Livro.css";


function Livro(props) {
    
    function nome(nome){
        if((nome.slice(0,nome.indexOf("/"))).length>30){
            return nome.slice(0,nome.indexOf("/"))
        }else{
            return (nome.slice(0,nome.indexOf("/"))).substring(0,30), " ..."
        }
        
    }
    return(
        
            <div className="Livro">
                <img src={props.livro.image}/>
                <h1>
                    {
                        nome(props.livro.name)
                    }
                    </h1>
            </div>

     );
}

export default Livro;
