import "./App.css";
import BarraPesquisa from "./compontente/BarraPesquisa/BarraPesquisa";
import Cabecalho from "./compontente/Cabecalho/Cabecalho";
import Categoria from "./compontente/Categoria/Categoria";
import axios from "axios";
import { useEffect, useState } from "react";
function App() {
  const [Livros, setLivros] = useState([{}]);
  var [Topicos, setTopicos] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/teste")
      .then((resposta) => {
        setLivros(resposta.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    axios
      .get("http://localhost:8080/oi")
      .then((resposta) => {
        setTopicos(resposta.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <>
      <Cabecalho />
      <BarraPesquisa />
      {Topicos.forEach((topico) => {
        return <Categoria categoria={topico}  livro={Livros.map((conteudo)=> {
          console.log(conteudo.topic, topico,"AAAAAAAAAAAAAAAAAAAAAAAAAAAAAaaaa")
          if(conteudo.topic==topico){
            return conteudo;  
          }
  })}
  />;
      })}
    </>
  );
}

export default App;
